import { Typography } from '@mui/material'
import { Box } from '@mui/system'
import React, { useEffect, useState, useContext } from 'react'
import Avatar from '@mui/material/Avatar';
import { styled } from '@mui/material/styles';
import Badge from '@mui/material/Badge';
import { ChatContext } from '../Context/ChatContext';
// import { ChatIDContext } from '../Context/ChatIDContext';
// import axios from '../axios'
// import { baseUrl } from '../url';
import { UserContext } from '../Context/Context';
import SearchIcon from '@mui/icons-material/Search';
import InputBase from '@mui/material/InputBase';
import socket from '../socket';
import './background/bg.css'

const StyledBox = styled(Box)({
    display: "flex",
    gap: 2,
    width: 380,
    alignItems: "center",
    borderRadius: 4,
})

const Search = styled('div')(({ theme }) => ({
    position: 'relative',
    borderRadius: '0.5rem',
    backgroundColor: 'white',
    '&:hover': {
        backgroundColor: 'white',
    },
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing(1),
        width: 'auto',
    },
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
    color: 'inherit',
    '& .MuiInputBase-input': {
        padding: theme.spacing(1, 1, 1, 0),
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + ${theme.spacing(4)})`,
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: '12ch',
            '&:focus': {
                width: '20ch',
            },
        },
    },
}));

const StyledBadge = styled(Badge)(({ theme }) => ({
    '& .MuiBadge-badge': {
        backgroundColor: '#44b700',
        color: '#44b700',
        boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
        '&::after': {
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            borderRadius: '50%',
            animation: 'ripple 1.2s infinite ease-in-out',
            border: '1px solid currentColor',
            content: '""',
        },
    },
    '@keyframes ripple': {
        '0%': {
            transform: 'scale(.8)',
            opacity: 1,
        },
        '100%': {
            transform: 'scale(2.4)',
            opacity: 0,
        },
    },
}));

function SideBar({ setName }) {
    const [users, setUsers] = useState([])
    const { user } = useContext(UserContext)
    const { setChat } = useContext(ChatContext)
    const [sideMsg, setSideMsg] = useState([])

    useEffect(() => {
        users.map((item) => {
            // console.log(item.Name);
            let msg = localStorage.getItem(item.Name)
            msg = JSON.parse(msg)
            if (msg) {
                setSideMsg(m => [...m, { to: item.Name, message: msg }])
            }

        })
    },[users])




    useEffect(() => {

        socket.on('activeUser', (data) => {
            setUsers([...data])
        })

    })


    return (

        <Box pt={13} >
            <Box sx={{
                width: { xs: "100%", sm: '35%', md: "25%" },
                height: "100%",
                bgcolor: "#f7f7f8",
                position: "fixed",
                mt: -1
            }}>
                <Box sx={{
                    overflowY: "scroll",
                    overflow: 'hidden',
                    mt: -1
                }}>
                    <Box>
                        <Box sx={{
                            display: 'flex',
                            flexDirection: 'column',
                            pr: '2.6%'
                        }}>
                            <Search sx={{ ml: '0.5rem', mt: '1rem' }}>
                                <SearchIconWrapper>
                                    <SearchIcon />
                                </SearchIconWrapper>
                                <StyledInputBase
                                    placeholder="Searchâ€¦"
                                    inputProps={{ 'aria-label': 'search' }}
                                />
                            </Search>
                            <Typography variant="h8" fontWeight="100" mt={2} ml={2}>Online Friends</Typography>
                        </Box>
                        {users.map((User) => (
                            <Box key={User.Name}>
                                {user.Name !== User.Name && (


                                    <StyledBox
                                        key={User.Name}
                                        height={70}
                                        sx={{
                                            width: '100%',
                                            '&:hover': {
                                                backgroundColor: '#E6E6E6',
                                            }
                                        }}
                                        pl={2}
                                        onClick={() => {
                                            setChat(User)
                                            setName(User.Name)
                                        }}
                                    >
                                        {/* <IconButton/> */}
                                        <StyledBadge
                                            overlap="circular"
                                            anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
                                            variant="dot"

                                        >
                                            <Avatar alt="Remy Sharp" src={User.imageURL} />
                                        </StyledBadge>
                                        <Box ml={2} >
                                            <Typography level='body1' color='#638bfa'>{User.Name}</Typography>
                                            {sideMsg.map((item,index) =>
                                                <Typography key={index} sx={{ fontSize: '0.9rem', color: '#808080' }}  >{item.to === User.Name && item.message}</Typography>
                                            )}
                                        </Box>
                                    </StyledBox>
                                )}
                            </Box>
                        ))}
                    </Box>
                </Box>
            </Box>
        </Box>

    )
}

export default SideBar
