import { Avatar, IconButton, Typography } from '@mui/material'
import MoreVertIcon from '@mui/icons-material/MoreVert';
import React, { useContext } from 'react'
import { Box } from '@mui/system';
import { ChatContext } from '../Context/ChatContext'
import { ChatIDContext } from '../Context/ChatIDContext';
import { baseUrl } from '../url';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';


function ChatHeader() {
    const { chat, setChat } = useContext(ChatContext)
    const { chatURL, setChatURL, setChatID } = useContext(ChatIDContext)

    const handleLeave = () => {
        setChat('')
        setChatURL('')
        setChatID('')
    }
    console.log(chatURL);

    return (
        <Box position="sticky">
            <Box sx={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                height: '4rem',
                pl: '1rem',
                pr: '1rem',
                mb: '-1rem'
            }} variant="dense" >
                <Box display="flex" alignItems="center" gap={2}>
                    <Box sx={{
                        display: "flex",
                        alignItems: "center",
                        pr: '0.5rem'
                    }}>
                        {chat && <IconButton ><ArrowBackIcon color='neutral' variant='plain' onClick={handleLeave} /></IconButton>}
                    </Box>
                    {chat && <Avatar alt="img" src={baseUrl + '/' + chatURL} />}
                    <Box>
                        <Typography sx={{ color: '#638bfa', fontSize: '1.2rem', lineHeight: '25px' }}>{chat}</Typography>
                        <Typography sx={{ fontSize: '0.9rem', color: '#808080', lineHeight: '8px' }}  >{chat && 'online'}</Typography>
                    </Box>
                </Box>
                <IconButton >
                    <MoreVertIcon sx={{ fontSize: 25 }} />
                </IconButton>
            </Box>
            <hr />
        </Box>
        // onClick={e => setOpen(true)}
    )
}

export default ChatHeader
