import React, { useContext, useEffect, useState } from 'react'
import { Box, Stack } from '@mui/system'
import AdminNavbar from '../components/AdminHeader'
import ChatHeader from '../components/ChatHeader'
import SideBar from '../components/SideBar'
import ChatBody from '../components/ChatBody'
import ChatFooter from '../components/ChatFooter'
import { UserContext } from '../Context/Context'
import { ChatContext } from '../Context/ChatContext';
import socket from '../socket'


function Chatpage() {
  const [messages, setMessages] = useState([])
  const { chat } = useContext(ChatContext)
  const { user, setUser } = useContext(UserContext)
  const [name, setName] = useState('')
  const map = new Map()
  const [sideMsg, setSideMsg] = useState([])
  const newMsg = []


  useEffect(() => {
    let User = JSON.parse(localStorage.getItem('user'))
    if (User) { setUser(User) }
    User && socket.emit('newUser', User)
  }, [setUser])

  useEffect(() => {
    socket.on(user.Name, (data) => {
      if (data.from === user.Name) {
        const arr = map.get(data.to) || []
        map.set(data.to, [...arr, data])
      }
      if (data.to === user.Name) {
        const arr = map.get(data.from) || []
        map.set(data.from, [...arr, data])
      }
      for (const [key, value] of map) {
        // const oldMsg = JSON.parse(localStorage.getItem(key))
        // const newMsg = [...oldMsg, value]
        // console.log(value);
        if (name === key) setMessages(m => [...m, value])
        if (name !== key) {
          if (newMsg.length <= 0) newMsg.push(value)
          newMsg.forEach((item) => {
            if (value.from === item.from && value.to === item.to) {
              item = value
            } else if (value.to === item.from && value.from === item.to) {
              item = value
            } else {
              newMsg.push(value)
            }
          })
          setSideMsg([...newMsg])
        }
      }
      // setMessages(msg => [...msg, data])
    })
  })




  return (
    <Box >
      <Stack direction="row" justifyContent="space-between">

        {chat ? (
          <Box sx={{ width: { xs: '100%', sm: '65%', md: "75%" }, display: { sm: 'none' } }} >
            <ChatHeader setMessages={setMessages} />
            <ChatBody messages={messages} setMessages={setMessages} name={name} />
            <ChatFooter />
          </Box>
        ) : (
          <Box sx={{ width: { xs: "100%", sm: '35%', md: "25%" }, display: { sm: 'none' } }}>
            <AdminNavbar user={user} />
            <SideBar setName={setName} sideMsg={sideMsg} />
          </Box>
        )}


        <Box sx={{ width: { xs: "100%", sm: '35%', md: "25%" }, display: { xs: 'none', sm: 'block' } }}>
          <AdminNavbar user={user} />
          <SideBar setName={setName} sideMsg={sideMsg} />
        </Box>

        <Box sx={{ width: { xs: '100%', sm: '65%', md: "75%" }, display: { xs: "none", sm: "block" } }} >
          <ChatHeader setMessages={setMessages} />
          <ChatBody messages={messages} setMessages={setMessages} name={name} />
          <ChatFooter />
        </Box>

      </Stack>
    </Box>
  )
}

export default Chatpage
