import { Button, Input, TextField, FormControl } from '@mui/material'
import { Box } from '@mui/system'
import React, { useState, useContext } from 'react'
import SendIcon from '@mui/icons-material/Send';
import { ChatIDContext } from '../Context/ChatIDContext';

function ChatFooter({ socket, user, userId }) {

    const [message, setMessage] = useState('');
    const { chatID } = useContext(ChatIDContext)
    let type = user
    let content = {
        text: message,
        user: user,
    }

    if (!chatID) {
        type = '';
        socket.emit('typing', type)
    }


    const handleSendMessage = (e) => {
        e.preventDefault();
        type = '';
        socket.emit('typing', type)
        if (message.trim() && chatID) {
            console.log('sended');
            socket.emit('private message', {
                content,
                to: chatID,
                from: userId
            })
        }
        setMessage('');

    };
    const handleTyping = () => {
        socket.emit('typing', type)
    }

    return (
        <Box sx={{ position: "fixed", bottom: "2%", width: { xs: "100%", sm: "75%", } }}>
            <hr />
            <Box sx={{ ml: '1rem', mr: '1rem' }}>
                <Box alignItems='center' component="form" onSubmit={handleSendMessage}>
                    <FormControl>
                        <Input placeholder="Placeholder" />
                    </FormControl>
                    {/* <Input
                        value={message}
                        name="text"
                        onChange={(e) => setMessage(e.target.value)}
                        onKeyDown={handleTyping}
                        sx={{ width: '75%', borderRadius: 4, pl: 1, bgcolor: '#f7f7f8' }}
                    />
                    <Button sx={{ width: '25%' }} variant="contained" type="submit" endIcon={<SendIcon />}>
                        Send
                    </Button> */}
                </Box>
            </Box>
        </Box>
    )
}

export default ChatFooter
