const getConnectedDevices = (type) => {
    navigator.mediaDevices.enumerateDevices()
        .then((devices) => {
            const filtered = devices.filter(e => e.kind === type)
            if (filtered && filtered.length > 0) {
                return filtered
            } else {
                return false
            }
        }).catch(err => {
            console.log('error : ', err);
        })
}
module.exports = { getConnectedDevices }
