import React, { useEffect, useState } from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
// import FormControlLabel from '@mui/material/FormControlLabel';
// import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
// import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import ForumOutlinedIcon from '@mui/icons-material/ForumOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import axios from '../axios'
import '../components/background/bg.css'
import { baseUrl } from '../url';
import { useNavigate } from 'react-router-dom'
import { Backdrop, CircularProgress } from '@mui/material';
import 'animate.css';
// import { UserContext } from '../Context/Context';
import './style.css'

function Copyright(props) {


  return (
    <Typography variant="body2" color="text.secondary" align="center" {...props}>
      {'Copyright Â© '}
      <Link color="inherit" href="https://mui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const theme = createTheme();


export default function SignIn({ socket }) {

  const navigate = useNavigate()
  const [genter, setGenter] = useState('')
  const [borderF, setBorderF] = useState(0)
  const [borderM, setBorderM] = useState(0)
  const [url, setUrl] = useState('')
  const [message, setMessage] = useState('')
  const [gen, setGen] = useState(true)
  const [open, setOpen] = useState(false)
  const male = 'https://www.shutterstock.com/image-vector/face-expression-handsome-young-man-260nw-1751161418.jpg'
  const female = 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRxMBXqUmPKxhTg1I3_gzp6Nuom9tFjq7bOqe3TNgiR&s'


  const handleClose = () => {
    setOpen(false);
  };

  useEffect(() => {
    let user = localStorage.getItem('user')
    if (user) return navigate('/chatpage')
  },[navigate])


  const handleLogin = (event) => {

    event.preventDefault();
    setOpen(!open);
    const data = new FormData(event.currentTarget);
    const loginData = {
      Name: data.get('name'),
      Genter: genter,
      imageURL: url
    }
    if (!loginData.Name) return setMessage('User name required!')
    if (!genter) return setGen(false)
    console.log(loginData);

    axios.post(`${baseUrl}/login`, loginData, { withCredentials: true }).then((res) => {
      console.log(res.data);
    if (res.data.msg) return setMessage(res.data.msg)
    localStorage.setItem('user', JSON.stringify(res.data))
    if (res.data) navigate('/chatpage')

    })
  };


  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box

          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
              <ForumOutlinedIcon />
            </Avatar>
            <Typography className='animate__zoomIn' sx={{ component: "h1", variant: "h5", color: "secondary.main" }}>CHIT CHAT</Typography>
          </Box>
          <Box component="form" onSubmit={handleLogin} noValidate sx={{ mt: 1 }}>
            <TextField
              margin="normal"
              required
              fullWidth
              id="name"
              label="User Name"
              name="name"
              autoComplete="name"
              autoFocus
              onChange={() => setMessage('')}
            />
            {message && <Typography sx={{ color: 'red' }}>{message}</Typography>}
            <Box sx={{
              display: 'flex',
              justifyContent: 'center',
              alignItems: 'center',
              gap: '1rem',
              mt: '1rem'
            }}>
              <Avatar
                alt="Remy Sharp"
                src={female}
                variant="rounded"
                onClick={() => {
                  setGen(true)
                  setGenter('female')
                  setUrl(female)
                  setBorderF(2)
                  setBorderM(0)
                }}
                sx={{ width: 100, height: 100, border: borderF }}
              />
              <Avatar
                alt="Remy Sharp"
                src={male}
                variant="rounded"
                onClick={() => {
                  setGen(true)
                  setGenter('male')
                  setUrl(male)
                  setBorderF(0)
                  setBorderM(2)
                }}
                sx={{ width: 100, height: 100, border: borderM }}
              />
            </Box>
            <Box sx={{ mt: '1rem' }}>
              {!gen && <Typography sx={{ color: 'red' }}>Select your genter!</Typography>}
            </Box>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              LOGIN
            </Button>
          </Box>
        </Box>
        <Backdrop
          sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
          open={open}
          onClick={handleClose}
        >
          <CircularProgress color="inherit" />
        </Backdrop>
        <Copyright sx={{ mt: 8, mb: 4 }} />
      </Container>
    </ThemeProvider>
  );
}
