import React, { useContext, useEffect, useRef, useState } from 'react'
import { Box, Stack } from '@mui/system'
import AdminNavbar from '../components/AdminHeader'
import ChatHeader from '../components/ChatHeader'
import SideBar from '../components/SideBar'
import ChatBody from '../components/ChatBody'
import ChatFooter from '../components/ChatFooter'
import axios from '../axios'
import { UserContext } from '../Context/Context'
import { ChatIDContext } from '../Context/ChatIDContext';
import { baseUrl } from '../url'


function Chatpage({ socket }) {
  const { setImageURL } = useContext(UserContext)
  const [messages, setMessages] = useState([])
  const [message, setMessage] = useState()
  const [user, setUser] = useState(null)
  const [typingStatus, setTypingStatus] = useState('')
  const lastMessageRef = useRef(null);
  const { chatID } = useContext(ChatIDContext)
  const { userId } = useContext(UserContext)

  useEffect(() => {

    axios.get(`${baseUrl}/auth`, { withCredentials: true }).then((response) => {
      if (response.data.loginGranted) {
        setUser(response.data.user.firstName)
        // setUserId(response.data.user._id)
        setImageURL(response.data.user.imageUrl)
        socket.on(userId, ({ content }) => {
          setMessage(content)
          console.log('start');
          // console.log(messages.includes(content));
          console.log('end');
          setMessages(messages => ([...messages, content]))

        })
      }
    })
  }, [socket])

  useEffect(() => {
    // ðŸ‘‡ï¸ scroll to bottom every time messages change
    lastMessageRef.current?.scrollIntoView({ behavior: 'smooth' });
    console.log(messages);

  }, [messages]);

  useEffect(() => {
    // send typing status to backend using socket.io
    socket.on('typingResponse', (data) => setTypingStatus(data));
  }, [socket]);

  return (
    <Box >
      <Stack direction="row" justifyContent="space-between">
        {userId ? 
        <Box sx={{ width: { xs: "100%", sm: '35%', md: "25%" }, display: { xs: "none", sm: "block" } }}>
          <AdminNavbar />
          {/* socket={socket} user={userId}  */}
          <SideBar />
          {/* socket={socket} useradmin={userId} message={message}  */}
        </Box>
        :
        (<Box sx={{ width: { xs: "100%", sm: '40%', md: "25%" }, display: { xs: "block" } }}>
          <AdminNavbar />
          {/* socket={socket} user={userId}  */}
          <SideBar  />
          socket={socket} useradmin={userId} message={message}
        </Box>)
         }
        {chatID ?
          (<Box sx={{ width: { xs: '100%', sm: '65%', md: "75%" }, }} >
            <ChatHeader />
            <ChatBody user={user} messages={messages} lastMessageRef={lastMessageRef} typingStatus={typingStatus} />
            <ChatFooter user={user} userId={userId} socket={socket} />
          </Box>)
          :
          (<Box sx={{ display: { xs: "none", sm: "block" }, width: '75%', }} >
            <ChatHeader />
            <ChatBody user={user} messages={messages} lastMessageRef={lastMessageRef} typingStatus={typingStatus} />
            <ChatFooter user={user} userId={userId} socket={socket} />
          </Box>)
        }
      </Stack>
    </Box>
  )
}

export default Chatpage
