import { Box, Typography } from '@mui/material'
import React, { useContext, useState } from 'react'
import { ChatContext } from '../Context/ChatContext';
import { UserContext } from '../Context/Context';
import socket from '../socket';
import './background/bg.css'

function ChatBody({ messages, lastMessageRef}) {
  const { chat } = useContext(ChatContext)
  const { user } = useContext(UserContext)
  const [typingStatus, setTypingStatus] = useState('')

  React.useEffect(() => {
    // send typing status to backend using socket.io

    socket.on(`type${user.Name}`, (data) => setTypingStatus(data));
  }, [user.Name]);



  return (

    <Box position='relative' zIndex={100} mt='4rem'>
      <Box>
        <Box sx={{ overflowY: "scroll", overflow: 'hidden' }}>
          <Box>
            <Box sx={{ pb: 8 }}>
              {/* ////// */}
              {chat ? (messages.map((message, index) =>
                message.from === user.Name ? (
                  <Box
                    key={index}
                    display="inline-flex"
                    width='100%'
                    justifyContent="right"
                    sx={{ p: 2, }}>
                    <Box>
                      <Typography align='right'>you</Typography>
                      <Box sx={{ bgcolor: "#c1d4f7", Width: "50%", p: 2, borderRadius: 4, fontSize: "2rem" }}>
                        <Typography sx={{ fontSize: '0.9rem', lineHeight: '8px' }}>{message.message}</Typography>
                      </Box>
                    </Box>
                  </Box>
                ) : (
                  <Box
                    key={index}
                    display="inline-flex"
                    width='100%'
                    justifyContent="left"
                    sx={{ p: 2, }}>
                    <Box>
                      <Typography align='left'>{message.from}</Typography>
                      <Box sx={{ bgcolor: "rgb(194, 243, 194)", Width: "50%", p: 2, borderRadius: 4, ml: 1 }}>
                        <Typography sx={{ fontSize: '0.9rem', lineHeight: '8px' }}>{message.message}</Typography>
                      </Box>
                    </Box>
                  </Box>
                ))) : (
                <Box sx={{ display: "flex", justifyContent: "center", pt: 20 }}>
                  <Typography>Chat is empty </Typography>
                </Box>
              )}

              <Box ref={lastMessageRef} />
            </Box>
            <Box sx={{ position: "fixed", bottom: "10%", left: { xs: "5%", sm: "41%" } }}>
              <Typography>{typingStatus && chat && `${typingStatus} is typing...`}</Typography>
            </Box>
          </Box>
        </Box>
      </Box>
    </Box>
  )
}

export default ChatBody
