import { Box } from '@mui/system'
import React, { useRef, useState } from 'react'
import { useEffect } from 'react'
const { getConnectedDevices, getDeviceChange } = require('../MediaQuery/MediaDevices')


function MyVideo() {
    const videoRef = useRef(null)
    const [devices, setDevices] = useState()
    const [count, setCount] = useState(0)



    useEffect(() => {
        getConnectedDevices('videoinput')
            .then(doc => setDevices(doc))
        setInterval(setCount(pre => pre + 1), 1000)
    }, [])

    useEffect(() => {
        console.log('eff');
        // setInterval()
        navigator.mediaDevices.addEventListener('devicechange', event => {
            console.log('change');
            console.log(getConnectedDevices('videoinput'))
        })
    }, [count])

    return (
        <Box sx={{
            display: 'flex',
            pl: 120
        }}>
            <Box>{count}</Box>
            <video id='video001' ref={videoRef} autoPlay playsInline controls={false}></video>
        </Box>
    )
}

export default MyVideo
