import { Button, Input, TextField, FormControl, IconButton } from '@mui/material'
import { Box } from '@mui/system'
import React, { useState, useContext } from 'react'
import SendIcon from '@mui/icons-material/Send';
import { ChatIDContext } from '../Context/ChatIDContext';
import Container from '@mui/material/Container';

function ChatFooter({ socket, user, userId }) {

    const [message, setMessage] = useState('');
    const { chatID } = useContext(ChatIDContext)
    let type = user
    let content = {
        text: message,
        user: user,
    }

    if (!chatID) {
        type = '';
        socket.emit('typing', type)
    }


    const handleSendMessage = (e) => {
        e.preventDefault();
        type = '';
        socket.emit('typing', type)
        if (message.trim() && chatID) {
            console.log('sended');
            socket.emit('private message', {
                content,
                to: chatID,
                from: userId
            })
        }
        setMessage('');

    };
    const handleTyping = () => {
        socket.emit('typing', type)
    }

    return (
        <Container>
            <Box sx={{ position: "fixed", bottom: "2%", width: { xs: "100%", sm: "62rem", } }}>
                <hr />
                <Box sx={{ ml: '1rem', mr: '1rem' }}>
                    <Box alignItems='center' component="form" onSubmit={handleSendMessage}>
                        <Input sx={{
                            bgcolor: '#f7f7f8',
                            width: '59.5rem',
                            height: '2.5rem',
                            borderRadius: 5,
                            pl: '2rem'
                        }}
                            placeholder="Write somthing....."
                            disableUnderline={true}
                            value={message}
                            name="text"
                            onChange={(e) => setMessage(e.target.value)}
                            onKeyDown={handleTyping}
                        />
                        <IconButton type="submit" color='primary' sx={{ ml: '-2.5rem' }}>
                            <SendIcon />
                        </IconButton>
                        {/* <Input
                        value={message}
                        name="text"
                        onChange={(e) => setMessage(e.target.value)}
                        onKeyDown={handleTyping}
                        sx={{ width: '75%', borderRadius: 4, pl: 1, bgcolor: '#f7f7f8' }}
                    />
                    <Button sx={{ width: '25%' }} variant="contained" type="submit" endIcon={<SendIcon />}>
                        Send
                    </Button> */}
                    </Box>
                </Box>
            </Box>
        </Container>
    )
}

export default ChatFooter
