import { Avatar, IconButton, Menu, MenuItem, Typography } from '@mui/material'
import MoreVertIcon from '@mui/icons-material/MoreVert';
import React, { useContext, useState } from 'react'
import { Box } from '@mui/system';
import { ChatContext } from '../Context/ChatContext'
import ArrowBackIcon from '@mui/icons-material/ArrowBack';



function ChatHeader({setMessages}) {
    const [open, setOpen] = useState(false)
    const { chat, setChat } = useContext(ChatContext)

    const handleLeave = () => {
        setChat('')
        setOpen(false)
        setMessages([])
        console.log('chathead');
    }

    return (
        <Box position="fixed" zIndex={1000} sx={{ width: {xs:'100%',sm:"65%", md: '75%' } }} >
            <Box sx={{
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                height: '4rem',
                pl: '1rem',
                pr: '1rem',
                mb: '-1rem',
                bgcolor: 'rgba(100%, 100%,100%, 0.1)',

            }} variant="dense" >
                <Box display="flex" alignItems="center" gap={2}>
                    <Box sx={{
                        display: "flex",
                        alignItems: "center"
                    }}>
                        {chat && <IconButton onClick={handleLeave} ><ArrowBackIcon color='neutral' variant='plain' /></IconButton>}
                    </Box>
                    {chat && <Avatar alt="img" src={chat.imageURL}/> }
                    <Box>
                        <Typography sx={{ color: '#638bfa', fontSize: '1.2rem', lineHeight: '25px' }}>{chat && chat.Name    }</Typography>
                        <Typography sx={{ fontSize: '0.9rem', color: '#808080', lineHeight: '8px' }}  >{chat && 'online'}</Typography>
                    </Box>
                </Box>
                {chat && <IconButton onClick={e => setOpen(true)}>
                    <MoreVertIcon sx={{ fontSize: 25 }} />
                </IconButton>}
            </Box>
            <hr />
            <Menu
                // sx={{
                //     mt: '45px',
                //     [theme.breakpoints.up('sm')]: {
                //         ml: "-75%"
                //     },

                //     [theme.breakpoints.down('sm')]: {
                //         ml: "none"
                //     },
                // }}
                id="menu-appbar"
                anchorOrigin={{
                    vertical: 'top',
                    horizontal: "right",
                }}
                keepMounted
                transformOrigin={{
                    vertical: 'top',
                    horizontal: 'right',
                }}
                open={open}
                onClose={e => setOpen(false)}
            >
                <MenuItem >
                    <Typography textAlign="center">View profile</Typography>
                </MenuItem>
                <MenuItem >
                    <Typography textAlign="center">Clear chat</Typography>
                </MenuItem>
                <MenuItem onClick={handleLeave}>
                    <Typography textAlign="center">Leave chat</Typography>
                </MenuItem>
            </Menu>
        </Box>



    )
}

export default ChatHeader
