const getConnectedDevices = (type) => {
    const connectedDevices = []
    navigator.mediaDevices.enumerateDevices()
        .then((devices) => {
            connectedDevices.push(...devices.filter(e => e.kind === type))
        }).catch(err => {
            console.log('getConnectedDevices error : ', err);
        })
    return connectedDevices
}
module.exports = { getConnectedDevices }
