import React, { useContext, useEffect, useRef, useState } from 'react'
import { Box, Stack } from '@mui/system'
import AdminNavbar from '../components/AdminHeader'
import ChatHeader from '../components/ChatHeader'
import SideBar from '../components/SideBar'
import ChatBody from '../components/ChatBody'
import ChatFooter from '../components/ChatFooter'
import { UserContext } from '../Context/Context'
import { ChatContext } from '../Context/ChatContext';


function Chatpage({ socket }) {
  // const { setImageURL } = useContext(UserContext)
  const [messages, setMessages] = useState([])
  // const [message, setMessage] = useState()
  // const [user, setUser] = useState(null)
  const [typingStatus, setTypingStatus] = useState('')
  const lastMessageRef = useRef(null);
  const { chat } = useContext(ChatContext)
  const { user, setUser } = useContext(UserContext)
  useEffect(() => {

    let User = JSON.parse(localStorage.getItem('user'))
    // console.log(User);
    if (User) setUser(User)
    User && socket.emit('newUser', User)
  }, [])

  useEffect(() => {
    socket.on(user.Name, (data) => {
      setMessages(messages => [...messages, data])
      console.log("recive msg");
      console.log(data);
    })
    socket.on(`type${user.Name}`, ({ type }) => {
      setTypingStatus(type)
    })
  }, [socket])



  // useEffect(() => {

  //   axios.get(`${baseUrl}/auth`, { withCredentials: true }).then((response) => {
  //     if (response.data.loginGranted) {
  //       setUser(response.data.user.firstName)
  //       // setUserId(response.data.user._id)
  //       setImageURL(response.data.user.imageUrl)
  //       socket.on(userId, ({ content }) => {
  //         setMessage(content)
  //         console.log('start');
  //         // console.log(messages.includes(content));
  //         console.log('end');
  //         setMessages(messages => ([...messages, content]))

  //       })
  //     }
  //   })
  // }, [socket])

  useEffect(() => {
    // 👇️ scroll to bottom every time messages change
    lastMessageRef.current?.scrollIntoView({ behavior: 'smooth' });
    console.log(messages);

  }, [messages]);



  return (
    <Box >
      <Stack direction="row" justifyContent="space-between">

        {chat ? (
          <Box sx={{ width: { xs: '100%', sm: '65%', md: "75%" }, display: { sm: 'none' } }} >
            <ChatHeader setMessages={setMessages} />
            <ChatBody messages={messages} lastMessageRef={lastMessageRef} typingStatus={typingStatus} />
            <ChatFooter />
          </Box>
        ) : (
          <Box sx={{ width: { xs: "100%", sm: '35%', md: "25%" }, display: { sm: 'none' } }}>
            <AdminNavbar user={user} />
            <SideBar />
          </Box>
        )}


        <Box sx={{ width: { xs: "100%", sm: '35%', md: "25%" }, display: { xs: 'none', sm: 'block' } }}>
          <AdminNavbar user={user} />
          <SideBar />
        </Box>

        <Box sx={{ width: { xs: '100%', sm: '65%', md: "75%" }, display: { xs: "none", sm: "block" } }} >
          <ChatHeader setMessages={setMessages} />
          <ChatBody messages={messages} lastMessageRef={lastMessageRef} typingStatus={typingStatus} />
          <ChatFooter />
        </Box>

      </Stack>
    </Box>
  )
}

export default Chatpage
