import { Typography } from '@mui/material'
import { Box } from '@mui/system'
import React, { useEffect, useState, useContext } from 'react'
import Avatar from '@mui/material/Avatar';
import { styled } from '@mui/material/styles';
import Badge from '@mui/material/Badge';
import { ChatContext } from '../Context/ChatContext';
import { UserContext } from '../Context/Context';
import SearchIcon from '@mui/icons-material/Search';
import InputBase from '@mui/material/InputBase';
import socket from '../socket';
import './background/bg.css'
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
// import Badge from '@mui/material/Badge';

const StyledBox = styled(Box)({
    display: "flex",
    gap: 2,
    width: 380,
    alignItems: "center",
    borderRadius: 4,
})

const Search = styled('div')(({ theme }) => ({
    position: 'relative',
    borderRadius: '0.5rem',
    backgroundColor: 'white',
    '&:hover': {
        backgroundColor: 'white',
    },
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing(1),
        width: 'auto',
    },
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
    color: 'inherit',
    '& .MuiInputBase-input': {
        padding: theme.spacing(1, 1, 1, 0),
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + ${theme.spacing(4)})`,
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: '12ch',
            '&:focus': {
                width: '20ch',
            },
        },
    },
}));

const StyledBadge = styled(Badge)(({ theme }) => ({
    '& .MuiBadge-badge': {
        backgroundColor: '#44b700',
        color: '#44b700',
        boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
        '&::after': {
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            borderRadius: '50%',
            animation: 'ripple 1.2s infinite ease-in-out',
            border: '1px solid currentColor',
            content: '""',
        },
    },
    '@keyframes ripple': {
        '0%': {
            transform: 'scale(.8)',
            opacity: 1,
        },
        '100%': {
            transform: 'scale(2.4)',
            opacity: 0,
        },
    },
}));

function SideBar({ setName, setMessages, name }) {
    const [users, setUsers] = useState([])
    const { user } = useContext(UserContext)
    const { setChat } = useContext(ChatContext)
    const [sideMsg, setSideMsg] = useState([{ from: 'Manu', to: 'guppy', message: 'hallo', time: '16:20' }])


    useEffect(() => {

        socket.on('activeUser', (data) => {
            setUsers([...data])
        })

    })

    useEffect(() => {
        socket.on(user.Name, (data) => {
            const newMsg = sideMsg
            let index = null
            if (newMsg.length === 0 && data.from !== user.Name) newMsg.push(data)
            newMsg.forEach((m, i) => {
                if (data.from === m.from && data.from !== user.Name) {
                    index = i
                }
            })
            if (index) newMsg[index] = data
            if (!index) newMsg.push(data)
            setSideMsg([...newMsg])
        })
    })

    useEffect(() => {
        const array = sideMsg
        if (name) {
            array.forEach((m, i) => {
                if (name === m.from) {
                    array.splice(i, 1)
                }
            })
            setSideMsg([...array])
        }
    }, [name, sideMsg])






    return (

        <Box pt={13} >
            <Box sx={{
                width: { xs: "100%", sm: '35%', md: "25%" },
                height: "100%",
                bgcolor: "#17191A",
                position: "fixed",
                mt: -1,

            }}>
                <Box sx={{
                    overflowY: "scroll",
                    overflow: 'hidden',
                    mt: -1,
                    height: '100%',
                    borderRight: '2px solid',
                    borderColor: 'grey.500',
                }}>
                    <Box>
                        <Box sx={{
                            display: 'flex',
                            flexDirection: 'column',
                            pr: '2.6%'
                        }}>
                            <Search sx={{
                                ml: '0.5rem',
                                mt: '1rem',
                                bgcolor: '#272930',
                                "&:hover": {
                                    bgcolor: '#272930'
                                }
                            }}>
                                <SearchIconWrapper>
                                    <SearchIcon sx={{ color: '#5B5E67' }} />
                                </SearchIconWrapper>
                                <StyledInputBase
                                    placeholder="Search…"
                                    inputProps={{ 'aria-label': 'search' }}
                                    sx={{ color: '#5B5E67' }}
                                />
                            </Search>
                            <Box sx={{
                                display: 'flex',
                                justifyContent: 'space-between',

                            }}>
                                <Box display='flex'>
                                    <Typography variant="h8" sx={{
                                        fontWeight: "500",
                                        fontSize: '1.4rem',
                                        fontFamily: 'sans-serif',
                                        color: 'white'
                                    }} mt={2} ml={4}><b>Messages </b></Typography>
                                    <Typography variant="h8" sx={{
                                        fontWeight: "400",
                                        fontSize: '1.3rem',
                                        fontFamily: 'sans-serif',
                                        color: '#548EF1'
                                    }} mt={2} ml={1}><b>({sideMsg.length})</b></Typography>
                                </Box>
                                <ChatBubbleOutlineIcon sx={{
                                    color: '#484A4C',
                                    mt: '20px',
                                    ml: 16
                                }} />
                            </Box>
                        </Box>
                        {users.map((User) => (
                            <Box key={User.Name}>
                                {user.Name !== User.Name && (
                                    <StyledBox
                                        key={User.Name}
                                        height={70}
                                        sx={{
                                            width: { xs: "100%", sm: '35%', md: "25%" },
                                            ml: 1,
                                            '&:hover': {
                                                backgroundColor: '#272930',
                                            }
                                        }}
                                        pl={4}
                                        onClick={() => {
                                            setChat(User)
                                            setName(User.Name)
                                            let sdmsg = sideMsg.find((m) => m.from === User.Name)
                                            sdmsg && setMessages([sdmsg])
                                        }}
                                    >
                                        <StyledBadge
                                            overlap="circular"
                                            anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
                                            variant="dot"
                                        >
                                            <Avatar sx={{
                                                width: 55,
                                                height: 55
                                            }}
                                                alt="Remy Sharp" src={User.imageURL} />
                                        </StyledBadge>
                                        <Box ml={2} >
                                            <Box sx={{
                                                display: 'flex',
                                                justifyContent: 'space-between'
                                            }}>
                                                <Typography level='body1' sx={{
                                                    color: 'white',
                                                    fontFamily: 'sans-serif',
                                                    fontWeight: "400",
                                                    fontSize: '1.2rem'
                                                }}><b>{User.Name}</b></Typography>
                                                {sideMsg.map((item) =>
                                                    item.from === User.Name && <Typography sx={{
                                                        color: '#424446',
                                                        ml: { xs: 15, sm: 15 }
                                                    }}>{item.time}</Typography>

                                                )}
                                            </Box>
                                            {sideMsg.map((item, index) =>
                                                <Box key={index} sx={{ display: 'flex', justifyContent: 'space-between' }}>
                                                    <Typography key={index} sx={{ fontSize: '0.9rem', color: 'white', fontFamily: 'sans-serif', }}  >{item.from === User.Name? item.message : 'no messages'}</Typography>
                                                    {item.from === User.Name && <Badge badgeContent={1} color='error'
                                                        sx={{
                                                            mr: 1.5,
                                                            mt: 1
                                                        }} />}
                                                </Box>
                                            )}
                                            {/* {sideMsg.length === 0 && <Typography sx={{ fontSize: '0.9rem', color: '#424446' }}  >{'no new message'}</Typography>} */}
                                        </Box>
                                    </StyledBox>
                                )}
                            </Box>
                        ))}
                    </Box>
                </Box>
            </Box>
        </Box>

    )
}

export default SideBar
