const getConnectedDevices = async (type) => {
    const connectedDevices = await navigator.mediaDevices.enumerateDevices()
    console.log('getdevice');
    console.log(connectedDevices);
    return connectedDevices.filter(d => d.kind === type)
}

const getDeviceChange = async (type) => {
    const changedDevices = await navigator.mediaDevices.addEventListener('devicechange', event => {
        return getConnectedDevices('videoinput')
    })
}
module.exports = { getConnectedDevices, getDeviceChange }
