import { Typography } from '@mui/material'
import { Box } from '@mui/system'
import React, { useEffect, useState, useContext } from 'react'
import Avatar from '@mui/material/Avatar';
import { styled, alpha } from '@mui/material/styles';
import Badge from '@mui/material/Badge';
import { ChatContext } from '../Context/ChatContext';
import { ChatIDContext } from '../Context/ChatIDContext';
import axios from '../axios'
import { baseUrl } from '../url';
import { UserContext } from '../Context/Context';
import SearchIcon from '@mui/icons-material/Search';
import InputBase from '@mui/material/InputBase';

const StyledBox = styled(Box)({
    display: "flex",
    gap: 2,
    width: 380,
    alignItems: "center",
    borderRadius: 4,
})

const Search = styled('div')(({ theme }) => ({
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: 'white',
    '&:hover': {
        backgroundColor: alpha(theme.palette.common.white, 0.25),
    },
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing(1),
        width: 'auto',
    },
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
    padding: theme.spacing(0, 2),
    height: '100%',
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
    color: 'inherit',
    '& .MuiInputBase-input': {
        padding: theme.spacing(1, 1, 1, 0),
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + ${theme.spacing(4)})`,
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: '12ch',
            '&:focus': {
                width: '20ch',
            },
        },
    },
}));

const StyledBadge = styled(Badge)(({ theme }) => ({
    '& .MuiBadge-badge': {
        backgroundColor: '#44b700',
        color: '#44b700',
        boxShadow: `0 0 0 2px ${theme.palette.background.paper}`,
        '&::after': {
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            borderRadius: '50%',
            animation: 'ripple 1.2s infinite ease-in-out',
            border: '1px solid currentColor',
            content: '""',
        },
    },
    '@keyframes ripple': {
        '0%': {
            transform: 'scale(.8)',
            opacity: 1,
        },
        '100%': {
            transform: 'scale(2.4)',
            opacity: 0,
        },
    },
}));

function SideBar({ socket, useradmin, message }) {
    const [users, setUsers] = useState([]);
    const { setChat, chat } = useContext(ChatContext)
    const { setChatID, setChatURL } = useContext(ChatIDContext)
    const { userId } = useContext(UserContext)

    useEffect(() => {
        axios.get(`${baseUrl}/activeUsers`, { withCredentials: true }).then((response) => {
            setUsers(response.data)
        }, [useradmin])

    });

    return (

        <Box pt={1} >
            <Box sx={{ width: { xs: "100%", sm: "30%" }, height: "100%", bgcolor: "#f7f7f8", position: "fixed", mt: -1 }}>
                <Box sx={{ overflowY: "scroll", overflow: 'hidden', mt: -1 }}>
                    <Box m={4} >
                        <Box sx={{
                            display: 'flex',
                            flexDirection: 'column'
                        }}>
                            <Search>
                                <SearchIconWrapper>
                                    <SearchIcon />
                                </SearchIconWrapper>
                                <StyledInputBase
                                    placeholder="Searchâ€¦"
                                    inputProps={{ 'aria-label': 'search' }}
                                />
                            </Search>
                            <Typography variant="h8" fontWeight="100" mt={4}>Online Friends</Typography>
                        </Box>
                        {users.map((user) => user.userId !== userId && (
                            <Box pr={4} mt={2} >
                                <StyledBox onClick={() => {
                                    setChat(user.userName)
                                    setChatID(user.userId)
                                    setChatURL(user.imageUrl)
                                }}
                                    bgcolor="#7691f5"
                                    height={70}
                                    sx={{ width: { xs: "100%", sm: "100%" } }}
                                    pl={2}
                                >
                                    <StyledBadge
                                        overlap="circular"
                                        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
                                        variant="dot"

                                    >
                                        <Avatar alt="Remy Sharp" src={baseUrl + '/' + user.imageUrl} />
                                    </StyledBadge>
                                    <Box ml={2} >
                                        <Typography color="black">{user.userName}</Typography>
                                        {message ? (<Typography color="white">{user.userName === message.user && !chat && message.text}</Typography>) : (<Typography color="grey">no new message</Typography>)}
                                    </Box>
                                </StyledBox>
                            </Box>
                        ))}
                    </Box>
                </Box>
            </Box>
        </Box>

    )
}

export default SideBar
