const getConnectedDevices = (type, callback) => {
    navigator.mediaDevices.enumerateDevices()
        .then((devices) => {
            console.log(devices);
            const filtered = devices.filter(e => e.kind === type)
            callback(filtered)
        }).catch(err => {
            console.log('getConnectedDevices error : ', err);
        })
}
module.exports = { getConnectedDevices }
